import { useWishlist } from "../context/wishlist-context";
import { showNotification } from "../Utilities/toast";
import "../styles.css";

export const Wishlist = () => {
  const { itemsInWishlist, setItemsInWishlist } = useWishlist();

  const removeFromWishlist = (currentList, itemId) => {
    showNotification("Removed from Wishlist");
    const currentItem = currentList.find((item) => item.id === itemId);
    const updatedList = itemsInWishlist.filter(
      (item) => item.id !== currentItem.id
    );
    setItemsInWishlist(updatedList);
  };

  return (
    <>
      <div className="flex flex-row-wrap">
        {itemsInWishlist.map((item) => (
          <div className="cont-fluid mg-1 pd-1 bdr-thin" key={item.id}>
            <img className="img-l" src={item.image} alt={item.name} />
            <div className="wishlist-details">
              <p className="txt-l txt-500">{item.name}</p>
              <p className="txt-l txt-500">{item.price}</p>
              <button
                className="pd-05 mg-05 bdr-thick bdr-blue bdr-rad-m btn btn-secondary-blue"
                onClick={() => removeFromWishlist(itemsInWishlist, item.id)}
              >
                Remove
              </button>
            </div>
          </div>
        ))}
        {itemsInWishlist.length === 0 && "wishlist is empty"}
        <div id="notification-container"></div>
      </div>
    </>
  );
};
